var request = require('request');
var options = {
  'method': 'GET',
  'url': 'https://resources.globiots.com/rest/api/public/v2/devices/host-name/{hostname}',
  'headers': {
    'Authorization': 'Basic ' + Buffer.from(YOUR_APP_KET + ':' + YOUR_SECRET_KET).toString('base64')
  }
};
request(options, function (error, response) {
  if (error) throw new Error(error);
  console.log(response.body);
});

